// going to show games
import React, { Component } from "react";
import GuessTheNote from "./games/GuessTheNote";
import Game from "./Game";
import { connect } from "react-redux";
import * as actions from "../actions";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      games: {}
    };
  }
  componentDidMount() {
    const token = localStorage.getItem("token");
    this.props.fetchGames({ id: token });
  }
  renderGames() {
    this.state.games = {
      // first this.props.games. is undefined thats why the error
      guessTheNote: {
        title: "GuessTheNote",
        description: "It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
        progress:
          (this.props.games.guessTheNote.correct * 100) /
          this.props.games.guessTheNote.tries
      },
      intervals: {
        title: "Intervals",
        description: "It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
        progress:
          (this.props.games.intervals.correct * 100) /
          this.props.games.intervals.tries
      },
      melody: {
        title: "Melody",
        description: "It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
        progress:
          (this.props.games.melody.correct * 100) /
          this.props.games.melody.tries
      },
      chords: {
        title: "Chords",
        description: "It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
        progress:
          (this.props.games.chords.correct * 100) /
          this.props.games.chords.tries
      }
    };
  }

  render() {
    console.log(this.props.games);
    if (Object.keys(this.props.games).length > 0) {
      this.renderGames();
    }
    return (
      <div className="dashboard">
        <Game game={this.state.games.guessTheNote} />
        <Game game={this.state.games.intervals} />
        <Game game={this.state.games.melody} />
        <Game game={this.state.games.chords} />
      </div>
    );
  }
}

function mapStateToProps({ games }) {
  // to be able to use this.props.auth
  return { games };
}

export default connect(
  mapStateToProps,
  actions
)(Dashboard);
