import React, { Component } from "react";
import { Route, Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { Dropdown, Button, NavItem, Navbar } from "react-materialize";
import "../style/header.css";

import * as actions from "../actions";

class Header extends Component {
  render() {
    const logout = () => {
      localStorage.removeItem("token");
      this.props.logout();
    };

    const navigation =
      this.props.auth.logged || localStorage.getItem("token") != null ? (
        <div>
          <NavItem>
            <Link to="/games">Exercises</Link>
          </NavItem>
          <NavItem>
            <Link to="/howto">How to practice</Link>
          </NavItem>
          <NavItem>
            <Link to="/contact">Contact</Link>
          </NavItem>
          <NavItem>
            <Link onClick={logout} to="/login" className="logout">
              Logout
            </Link>
          </NavItem>
        </div>
      ) : (
        <div>
          <NavItem>
            <Link to="/howto">How to practice</Link>
          </NavItem>
          <NavItem>
            <Link to="/contact">Contact</Link>
          </NavItem>
          <NavItem>
            <Link to="/signup">Sign up</Link>
          </NavItem>
          <NavItem>
            <Link to="/login" className="login">
              Login
            </Link>
          </NavItem>
        </div>
      );

    return (
      // <nav>
      //   <div class="nav-wrapper blue">
      //     <Link to="/" className="brand-logo">
      //       Musicly
      //     </Link>
      //     {navigation}
      //   </div>
      // </nav>
      <Navbar brand="Musicly" className="header" right>
        {navigation}
      </Navbar>
    );
  }
}

function mapStateToProps({ auth }) {
  // to be able to use this.props.auth
  return { auth };
}

export default connect(
  mapStateToProps,
  actions
)(Header);
