import React, { Component } from "react";
import { octaves, all } from "../assets/notes/notes";
import "./GuessTheNote.css";
import axios from "axios";

class GuessTheNote extends Component {
  constructor(props) {
    super(props);
    this.state = {

        C: "blue",
        D: "blue",
        E: "blue",
        F: "blue",
        G: "blue",
        A: "blue",
        B: "blue"

    };
  }
  render() {

    let clear = false;
    let progress = "not assigned";
    getProgress();
    let answer = localStorage.getItem("guessTheNote");

    if (answer == null) {
      localStorage.setItem("guessTheNote", 
        Math.floor(Math.random() * all.length)
      );
    }

    // if (answer != null) {
    //   answer = localStorage.getItem("guessTheNote");
    // }

    const right = async () => {
      const res = await axios.post("/api/updateProgress", {
        id: localStorage.getItem("token"),
        correct: true,
        game: "guessTheNote"
      });
      return res.data;
    };

    const wrong = async name => {
      const res = await axios.post("/api/updateProgress", {
        id: localStorage.getItem("token"),
        correct: false,
        game: "guessTheNote"
      });
      return res.data;
    };

    const change = name => {
      this.setState({ [name]: "red"  });
    };

    const getProgress = async () => {
      const res = await axios.post("/api/progress", {
        id: localStorage.getItem("token"),
        game: "guessTheNote"
      });

      progress = res.data.progress;
      console.log(res.data.progress + " progress:");
    };



    const playAnswer = (sound, name) => {
      const audio = new Audio(sound);
      audio.play();
      if (name === all[answer.answer].name) {
        console.log("right");
        // if the user's right
        clear = true;
        progress = right();
      }
      if (name !== all[answer.answer].name) {
        console.log("wrong");
        // if the user's wrong
        progress = wrong(name);
        change(name);
      }
    };

    const playQuestion = note => {
      const audio = new Audio(note);
      audio.play();
    };

    const buttons = octaves.c3.map(note => {
      return (
        <button
          onClick={() => playAnswer(note.sound, note.name)}
          className={
            "btn  sound waves-effect waves-light " +
            this.state[note.name]
          }
        >
          {note.name}
        </button>
      );
    });

    return (
      <div className="guessTheNote">
        <div className="question">
          <button
            onClick={() => playQuestion(all[answer].sound)}
            className="btn teal sound waves-effect waves-light"
          >
            ?
          </button>
          <p style={{ fontSize: "1.5em" }}>Click the "?" to hear again</p>
          <p> progress: {progress}%</p>
        </div>
        <div className="answers">{buttons}</div>
        <p style={{ fontSize: "1.5em" }}>
          Click the note that fits to the sound you just heard.
        </p>
      </div>
    );
  }
}

export default GuessTheNote;
