// going to show games
import React, { Component } from "react";
import GuessTheNote from "./games/GuessTheNote";
import Game from "./Game";
import { connect } from "react-redux";
import * as actions from "../actions";

class Dashboard extends Component {
  componentDidMount() {
    const token = localStorage.getItem("token");

    this.props.fetchGames({
      id: token
    });
  }

  render() {
    console.log(this.props.games);
    const games = {
      guessTheNote: {title:"", description:'', progress: this.props.games.guessTheNote.correct*100/this.props.games.guessTheNote.try}

    }
    return (
      <div className="dashboard">
        <Game guessTheNote={this.props.games.guessTheNote} />
        <Game intervals={this.props.games.intervals} />
        <Game melody={this.props.games.melody} />
        <Game chords={this.props.games.chords} />
      </div>
    );
  }
}

function mapStateToProps({ games }) {
  // to be able to use this.props.auth
  return { games };
}

export default connect(
  mapStateToProps,
  actions
)(Dashboard);
