import React, { Component } from "react";
import { octaves, all } from "../assets/notes/notes";
import './GuessTheNote.css'

class GuessTheNote extends Component {
  render() {
    const playSound = note => {
      const audio = new Audio(note);
      audio.play();
    };

    const buttons = octaves.c3.map(note => {
      return (
        <button onClick={() => playSound(note.sound)} className="btn blue sound waves-effect waves-light"> {note.name} </button>

      );
    });

    return <div className="guessTheNote">{buttons}</div>;
  }
}

export default GuessTheNote;
