import React, { Component } from "react";
import { Route, Redirect } from "react-router";
import axios from "axios";

class Login extends Component {
  render() {
    const style = { textAlign: "center" };
    const flag = false;
    const redirect = () => {
      axios.post("/auth/signup", { redirect: true }).then(data => {
        if (data.redirect) {
          flag = true;
        }
      });
    };

    const toLogin = flag ? <Redirect to="/login" /> : "";

    return (
      <div className="login">
        {toLogin}
        <h1 style={style}>Sign-up form</h1>
        <button onClick={redirect}>Redirect to login</button>
      </div>
    );
  }
}

export default Login;
