import React, { Component } from "react";
import { Route, Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { Dropdown, Button, NavItem, Navbar } from "react-materialize";
import "../style/header.css";

import * as actions from "../actions";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: ""
    };
    this.redirect = this.redirect.bind(this);
  }

  redirect(route) {

    this.setState({ redirect: <Redirect to={route} /> });
  }

  render() {
    const logout = () => {
      localStorage.removeItem("token");
      this.props.logout();
    };

    const navigation =
      this.props.auth.logged || localStorage.getItem("token") != null ? (
        <div>
          <NavItem className="no-links">
            <Link
              onClick={() => {
                const to = "/games";
                this.redirect(to);
              }}
            >
              Exercises
            </Link>
          </NavItem>
          <NavItem className="no-links">
            <Link onClick={() => this.redirect("howto")}>How to practice</Link>
          </NavItem>
          <NavItem className="no-links">
            <Link onClick={() => this.redirect("contact")}>Contact</Link>
          </NavItem>
          <NavItem className="no-links">
            <Link
              onClick={() => {
                logout();
                this.redirect("login");
              }}
              className="logout"
            >
              Logout
            </Link>
          </NavItem>
        </div>
      ) : (
        <div>
          <NavItem className="no-links">
            <Link onClick={() => this.redirect("howto")}>How to practice</Link>
          </NavItem>
          <NavItem className="no-links">
            <Link onClick={() => this.redirect("contact")}>Contact</Link>
          </NavItem>
          <NavItem className="no-links">
            <Link onClick={() => this.redirect("signup")}>Sign up</Link>
          </NavItem>
          <NavItem className="no-links">
            <Link onClick={() => this.redirect("login")} className="login">
              Login
            </Link>
          </NavItem>
        </div>
      );

    return (
      <Navbar
        brand="Musicly"
        className="header blue"
        right
        style={{ padding: "0 40px" }}
      >
        {navigation}
        {this.state.redirect}
      </Navbar>
    );
  }
}

function mapStateToProps({ auth }) {
  // to be able to use this.props.auth
  return { auth };
}

export default connect(
  mapStateToProps,
  actions
)(Header);
