import React, { Component } from "react";
import axios from "axios";
import { login, logout } from "../LocalStorage";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: "",
      username: "",
      password: ""
    };
  }
  render() {
    const handleSubmit = async () => {
      const res = await axios.post("/api/login", {
        username: this.state.username,
        password: this.state.password
      });

      if (!res.exist)
      this.setState({error: "wrong username or password, please try again."})

      if (res.exist){
        login();

        // tell redux that user is logged in and change components behavior according to that
      }      
      const token = localStorage.getItem("token");
      console.log(token);
    };

    const handleChange = event => {
      this.setState({ [event.target.name]: event.target.value });
    };

    const style = { textAlign: "center" };
    return (
      <div className="login">
        <h1 style={style}>Login!</h1>
        <form>
          <label>Username: </label>
          <input
            type="text"
            onChange={handleChange}
            name="username"
            value={this.state.username}
          />
          <label>Password: </label>
          <input
            type="text"
            onChange={handleChange}
            name="password"
            value={this.state.password}
          />
        </form>
        <button onClick={handleSubmit}>Login!</button>
        {this.state.error}
      </div>
    );
  }
}

export default Login;
