// going to show games
import React, { Component } from "react";
import GuessTheNote from "./games/GuessTheNote";
import Game from "./Game";
import { connect } from "react-redux";
import * as actions from "../actions";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      games: {}
    };
  }
  componentDidMount() {
    const token = localStorage.getItem("token");
    this.props.fetchGames({ id: token });
  }
  renderGames() {
    this.state.games = {
      // first this.props.games. is undefined thats why the error
      guessTheNote: {
        resetTitle: 'guessTheNote',
        route: '/guessthenote',
        title: "Guess The Note",
        description:
          "You'll hear sound of a random note and guess which note it was.",
        progress:
          (this.props.games.guessTheNote.correct * 100) /
          this.props.games.guessTheNote.tries
      },
      intervals: {
        resetTitle: 'intervals',        
        route: '/intervals',        
        title: "Intervals",
        description:
          "You'll hear a sequence of 2 notes and decide whether the interval between those 2 notes is major 3rd, perfect 5th or an octave.",
        progress:
          (this.props.games.intervals.correct * 100) /
          this.props.games.intervals.tries
      },
      melody: {
        resetTitle: 'melody',        
        route: '/melody',         
        title: "Melody chain",
        description:
          "You'll hear a sequence of 2-3-4 notes from a given scale and try to repeat the notes.",
        progress:
          (this.props.games.melody.correct * 100) /
          this.props.games.melody.tries
      },
      chords: {
        resetTitle: 'chords',        
        route: '/chords',                 
        title: "Chords",
        description:
          "You'll hear a chord and try to guess how many notes the chord contains.",
        progress:
          (this.props.games.chords.correct * 100) /
          this.props.games.chords.tries
      }
    };
  }

  render() {
    console.log(this.props.games);
    if (Object.keys(this.props.games).length > 0) {
      this.renderGames();
    }
    return (
      <div className="dashboard">
        <Game game={this.state.games.guessTheNote} />
        <Game game={this.state.games.intervals} />
        <Game game={this.state.games.melody} />
        <Game game={this.state.games.chords} />
      </div>
    );
  }
}

function mapStateToProps({ games }) {
  // to be able to use this.props.auth
  return { games };
}

export default connect(
  mapStateToProps,
  actions
)(Dashboard);
