import React, { Component } from "react";
import { Route, Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { Dropdown, Button, NavItem, Navbar } from "react-materialize";
import "../style/header.css";

import * as actions from "../actions";

class Header extends Component {
  render() {
    const logout = () => {
      localStorage.removeItem("token");
      this.props.logout();
    };

    const navigation =
      this.props.auth.logged || localStorage.getItem("token") != null ? (
        <div>
          <NavItem to="/games">Exercises</NavItem>

          <NavItem to="/howto">How to practice</NavItem>

          <NavItem to="/contact">Contact</NavItem>

          <NavItem onClick={logout} to="/login" className="logout">
            Logout
          </NavItem>
        </div>
      ) : (
        <div>
          <NavItem href="/howto">How to practice</NavItem>

          <NavItem to="/contact">Contact</NavItem>

          <NavItem to="/signup">Sign up</NavItem>

          <NavItem to="/login" className="login">
            Login
          </NavItem>
        </div>
      );

    return (
      <Navbar brand="Musicly" className="header blue" right style={{padding: '0 40px'}}>
        {navigation}
      </Navbar>
    );
  }
}

function mapStateToProps({ auth }) {
  // to be able to use this.props.auth
  return { auth };
}

export default connect(
  mapStateToProps,
  actions
)(Header);
