import React, { Component } from "react";
import { Route, Redirect, Link } from "react-router-dom";
import { connect } from "react-redux";
import { Dropdown, Button, NavItem, Navbar } from "react-materialize";
import "../style/header.css";

import * as actions from "../actions";

class Header extends Component {
  render() {
    const logout = () => {
      localStorage.removeItem("token");
      this.props.logout();
    };

    const navigation =
      this.props.auth.logged || localStorage.getItem("token") != null ? (
        <ul class="right headerButtons">
          <li>
            <Link to="/games">Exercises</Link>
          </li>
          <li>
            <Link to="/howto">How to practice</Link>
          </li>
          <li>
            <Link to="/contact">Contact</Link>
          </li>
          <li>
            <Link onClick={logout} to="/login" className="logout">
              Logout
            </Link>
          </li>
        </ul>
      ) : (
        <ul id="nav" class="right headerButtons">
          <li>
            <Link to="/howto">How to practice</Link>
          </li>
          <li>
            <Link to="/contact">Contact</Link>
          </li>
          <li>
            <Link to="/signup">Sign up</Link>
          </li>
          <li>
            <Link to="/login" className="login">
              Login
            </Link>
          </li>
        </ul>
      );

    return (
      <nav>
        <div class="nav-wrapper blue">
          <Link to="/" className="brand-logo">
            Musicly
          </Link>
          {navigation}
        </div>
      </nav>
      // <Navbar brand="Musicly" className="blue" right>
      //   {/* {navigation} */}
      //   <NavItem onClick={() => console.log("test click")}>
      //     Getting started
      //   </NavItem>
      //   <NavItem href="components.html">Components</NavItem>
      // </Navbar>
    );
  }
}

function mapStateToProps({ auth }) {
  // to be able to use this.props.auth
  return { auth };
}

export default connect(
  mapStateToProps,
  actions
)(Header);
