const login = () => {
  if (typeof(Storage) !== "undefined") {
    localStorage.setItem("token", "akfl1sabf2lakb3fkaln2khl4h2kjbf");
  } else {
    console.log("Sorry! No Web Storage support..");
  }
};

const logout = () => {
  localStorage.removeItem("token");
};

export { login, logout };
