import React, { Component } from "react";
import { octaves, all } from "../assets/notes/notes";
import "./GuessTheNote.css";

class GuessTheNote extends Component {
  constructor(props) {
    super(props)
    this.state = {
      clear: false
    }
  }
  render() {
    const playSound = note => {
      const audio = new Audio(note);
      audio.play();
    };

    const buttons = octaves.c3.map(note => {
      return (
        <button
          onClick={() => playSound(note.sound)}
          className="btn blue sound waves-effect waves-light"
        >
          {note.name}
        </button>
      );
    });

    return (
      <div className="guessTheNote">
        <div className="question">
          <button
            // onClick={}
            className="btn teal sound waves-effect waves-light"
          >
            ?
          </button>
          <p style={{fontSize: '1.5em'}}>Click the "?" to hear again</p>
        </div>
        <div className="answers">{buttons}</div>
        <p style={{fontSize: '1.5em'}}>Click the note that fits to the sound you just heard.</p>

      </div>
    );
  }
}

export default GuessTheNote;
