// going to show games
import React, { Component } from "react";
import GuessTheNote from "./games/GuessTheNote";
import Game from "./Game";
import { connect } from "react-redux";
import * as actions from "../actions";

class Dashboard extends Component {
  componentDidMount() {
    const token = localStorage.getItem("token");
    const fetchG = async () => {
      await this.props.fetchGames({
        id: token
      });
    };
    fetchG();
  }
  renderGames() {
    if(this.props.games !== {})
    return {
      // first this.props.games. is undefined thats why the error
      guessTheNote: {
        title: "GuessTheNote",
        description: "",
        progress:
          (this.props.games.guessTheNote.correct * 100) /
          this.props.games.guessTheNote.tries
      },
      intervals: {
        title: "Intervals",
        description: "",
        progress:
          (this.props.games.intervals.correct * 100) /
          this.props.games.intervals.tries
      },
      melody: {
        title: "Melody",
        description: "",
        progress:
          (this.props.games.melody.correct * 100) /
          this.props.games.melody.tries
      },
      chords: {
        title: "Chords",
        description: "",
        progress:
          (this.props.games.chords.correct * 100) /
          this.props.games.chords.tries
      }
    };
    else return {

    };
  }

  render() {
    console.log(this.props.games);
    const games = this.renderGames;
    return (
      <div className="dashboard">
        <Game game={games.guessTheNote} />
        <Game game={games.intervals} />
        <Game game={games.melody} />
        <Game game={games.chords} />
      </div>
    );
  }
}

function mapStateToProps({ games }) {
  // to be able to use this.props.auth
  return { games };
}

export default connect(
  mapStateToProps,
  actions
)(Dashboard);
