// going to show games
import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import "../style/game.css";

class Game extends Component {
  render() {
    const redirect = '';
    const start= ()=> {
      redirect = <Redirect to={'/' + this.props.game.title}/>
    }
    return (
      <div className="card">
      {redirect}
        <div className="card-image-title">
          <h1 className="card-title marginLeft">
            {this.props.game  !== undefined ? this.props.game.title : ""}
          </h1>
        </div>
        <div className="card-content marginLeft">
          <p>
            {this.props.game !== undefined ? this.props.game.description : ""}
          </p>
        </div>
        <div className="card-action">
          <button className="play marginLeft btn left blue">Play</button>
          <p className="">{this.props.game  === undefined || isNaN(this.props.game.progress) ?  "0" : this.props.game.progress}%</p>

          <button className="reset btn right blue">Reset</button>
        </div>
      </div>
    );
  }
}

export default Game;
