import React, { Component } from "react";
import { octaves, all } from "../assets/notes/notes";

class Intervals extends Component {
  constructor(props) {
    super(props);
    this.state = {
      difficulty: "1"
    };
    this.difficulty = this.difficulty.bind(this);
  }

  clearColors() {
    console.log(this.state.difficulty);
  }
  difficulty(e) {
    this.clearColors();
    this.setState({ difficulty: e.target.id, newSound: true });
  }

  render() {
    const level = this.state.difficulty;
    return (
      <div className="intervals">
        This is the Intervals game!
        <p style={{ color: "teal", fontSize: "1.5em" }}>Level: {level}</p>
        <div className="difficulties">
          <button
            className="btn waves-effect waves-light"
            id="1"
            onClick={this.difficulty}
          >
            Easy
          </button>
          <button
            className="btn waves-effect waves-light"
            id="2"
            onClick={this.difficulty}
          >
            Medium
          </button>
          <button
            className="btn waves-effect waves-light"
            id="3"
            onClick={this.difficulty}
          >
            Hard
          </button>
        </div>
      </div>
    );
  }
}

export default Intervals;
