// going to show games
import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import axios from "axios";
import "../style/game.css";

class Game extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: "",
      rerender: false
    };
  }
  render() {
    const start = () => {
      this.setState({
        redirect: <Redirect to={this.props.game.route} />
      });
    };
    const reset = () => {
      console.log("reset the pregress");
      axios.post('/api/reset', {id: localStorage.getItem('token'), name: this.props.game.resetTitle})
      setState({ rerender: !rerender });
    };
    return (
      <div className="card">
        <div className="card-image-title">
          <h1 className="card-title marginLeft">
            {this.props.game !== undefined ? this.props.game.title : ""}
          </h1>
        </div>
        <div className="card-content marginLeft">
          <p>
            {this.props.game !== undefined ? this.props.game.description : ""}
          </p>
        </div>
        <div className="card-action">
          <button className="play marginLeft btn left blue" onClick={start}>
            Play
          </button>
          <p className="">
            {this.props.game === undefined || isNaN(this.props.game.progress)
              ? "0"
              : Math.floor(this.props.game.progress)}
            %
          </p>

          <button className="reset btn right blue" onClick={reset()}>
            Reset
          </button>
        </div>
        {this.state.redirect}
      </div>
    );
  }
}

export default Game;
