import React, { Component } from "react";
import axios from "axios";
import { octaves, all } from "../assets/notes/notes";
import "./Intervals.css";

class Intervals extends Component {
  constructor(props) {
    super(props);
    this.state = {
      difficulty: "1",
      second: "blue",
      third: "blue",
      fifth: "blue",
      seventh: "blue",
      octave: "blue"
    };
    this.difficulty = this.difficulty.bind(this);
  }

  change(name, correct) {
    if (!correct) {
      axios.post("/api/updateProgress", {
        game: "guessTheNote",
        correct: false,
        id: localStorage.getItem("token"),
        level: this.state.difficulty
      });
      this.setState({ [name]: "red" });
    }

    if (correct) {
      axios.post("/api/updateProgress", {
        game: "guessTheNote",
        correct: true,
        id: localStorage.getItem("token"),
        level: this.state.difficulty
      });
      this.setState({ [name]: "green" });
    }
  }

  difficulty(e) {
    this.setState({
      difficulty: e.target.id,
      // newSound: true,
      second: "blue",
      third: "blue",
      fifth: "blue",
      seventh: "blue",
      octave: "blue"
    });
  }

  render() {
    const level = this.state.difficulty;
    return (
      <div className="intervals">
        This is the Intervals game!
        <p style={{ color: "teal", fontSize: "1.5em" }}>Level: {level}</p>
        <div className="difficulties">
          <button
            className="btn waves-effect waves-light"
            id="1"
            onClick={this.difficulty}
          >
            Easy
          </button>
          <button
            className="btn waves-effect waves-light"
            id="2"
            onClick={this.difficulty}
          >
            Medium
          </button>
          <button
            className="btn waves-effect waves-light"
            id="3"
            onClick={this.difficulty}
          >
            Hard
          </button>
        </div>
      </div>
    );
  }
}

export default Intervals;
